// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

contract Notary is Ownable {
    mapping(address => bool) public validators;

    IERC20 public token;

    struct UTXO {
        address owner;
        uint256 amount;
        bool spent;
    }

    UTXO[] public utxos;
    mapping(address => uint256[]) public userUTXOIndexes;


    constructor(address[] memory _validators, address _tokenAddress)  {
        for (uint256 i = 0; i < _validators.length; i++) {
            validators[_validators[i]] = true;
        }
        token = IERC20(_tokenAddress);
    }


    function validateTransaction(address sender) external view returns (bool) {
        return validators[sender];
    }

    function deposit(uint256 amount) external {
        require(amount > 0, "Amount must be greater than zero");
        require(token.transferFrom(msg.sender, address(this), amount), "Transfer failed");

        utxos.push(UTXO({
            owner: msg.sender,
            amount: amount,
            spent: false
        }));
        userUTXOIndexes[msg.sender].push(utxos.length - 1);
    }

    // function withdraw(uint256[] calldata utxoIndexes, address recipient, uint256 amount) external {
    //     require(amount > 0, "Amount must be greater than zero");

    //     uint256 totalInput = 0;

    //     for (uint256 i = 0; i < utxoIndexes.length; i++) {
    //         uint256 index = utxoIndexes[i];
    //         UTXO storage utxo = utxos[index];

    //         require(!utxo.spent, "UTXO already spent");
    //         require(utxo.owner == msg.sender, "Not the owner of this UTXO");

    //         totalInput += utxo.amount;
    //         utxo.spent = true; 
    //     }

    //     require(totalInput >= amount, "Insufficient UTXO balance");

    //     if (totalInput > amount) {
    //         utxos.push(UTXO({
    //             owner: msg.sender,
    //             amount: totalInput - amount,
    //             spent: false
    //         }));
    //     }

    //     require(token.transfer(recipient, amount), "Transfer failed");
    // }

    function withdraw(uint256 amount, address recipient) external {
        require(amount > 0, "Amount must be greater than zero");
        require(recipient != address(0), "Recipient cannot be zero address");

        uint256 totalInput = 0;
        uint256[] storage userIndexes = userUTXOIndexes[msg.sender];
        uint256[] memory selectedIndexes = new uint256[](userIndexes.length);
        uint256 selectedCount = 0;

        for (uint256 i = 0; i < userIndexes.length; i++) {
            uint256 index = userIndexes[i];
            UTXO storage utxo = utxos[index];

            if (!utxo.spent && utxo.owner == msg.sender) {
                totalInput += utxo.amount;
                selectedIndexes[selectedCount] = index;
                selectedCount++;

                if (totalInput >= amount) {
                    break;
                }
            }
        }

        require(totalInput >= amount, "Insufficient UTXO balance");

        uint256 newLength = 0;

        for (uint256 i = 0; i < userIndexes.length; i++) {
            uint256 index = userIndexes[i];
            bool isSpent = false;

            for (uint256 j = 0; j < selectedCount; j++) {
                if (index == selectedIndexes[j]) {
                    utxos[index].spent = true;
                    isSpent = true;
                    break;
                }
            }

            if (!isSpent) {
                userIndexes[newLength] = index;
                newLength++;
            }
        }


        while (userIndexes.length > newLength) {
            userIndexes.pop();
        }

        if (totalInput > amount) {
            utxos.push(UTXO({
                owner: msg.sender,
                amount: totalInput - amount,
                spent: false
            }));
            userIndexes.push(utxos.length - 1);
        }

        require(token.transfer(recipient, amount), "Transfer failed");
    }


    function getBalance(address user) external view returns (uint256 balance) {
        require(user != address(0), "Invalid address");

        balance = 0;
        uint256[] storage userIndexes = userUTXOIndexes[user];

        for (uint256 i = 0; i < userIndexes.length; i++) {
            uint256 index = userIndexes[i];
            UTXO storage utxo = utxos[index];

            if (!utxo.spent) {
                balance += utxo.amount;
            }
        }

        return balance;
    }

}

